#!/bin/bash

set -e

# Colors for output
RED='\033[0;31m'
GREEN='\033[0;32m'
YELLOW='\033[1;33m'
BLUE='\033[0;34m'
NC='\033[0m' # No Color

echo -e "${BLUE}📦 Deploying USDC and Uniswap V3 contracts...${NC}"

# Default RPC URL for L2
L2_RPC_URL=${L2_RPC_URL:-"http://localhost:8545"}

# Pre-funded account from OP Stack devnet (typical default)
DEPLOYER_PRIVATE_KEY="0xac0974bec39a17e36ba4a6b4d238ff944bacb478cbed5efcae784d7bf4f2ff80"
DEPLOYER_ADDRESS="0xf39Fd6e51aad88F6F4ce6aB8827279cffFb92266"

# Create deployment directory
mkdir -p data/contracts

echo -e "${YELLOW}Deploying contracts to L2 at $L2_RPC_URL...${NC}"

# Deploy USDC contract
echo -e "${YELLOW}Deploying USDC contract...${NC}"
USDC_ADDRESS=$(cast send --rpc-url $L2_RPC_URL --private-key $DEPLOYER_PRIVATE_KEY --create --constructor-args "USD Coin" "USDC" 6 $(cast to-uint256 1000000000000000) $DEPLOYER_ADDRESS $DEPLOYER_ADDRESS $DEPLOYER_ADDRESS --value 0  | grep "contractAddress" | cut -d'"' -f4 | head -1)

if [ -z "$USDC_ADDRESS" ]; then
    echo -e "${RED}❌ Failed to deploy USDC contract${NC}"
    exit 1
fi

echo "USDC_ADDRESS=$USDC_ADDRESS" > data/contracts/addresses.env
echo -e "${GREEN}✅ USDC deployed at: $USDC_ADDRESS${NC}"

# Deploy Uniswap V3 Factory
echo -e "${YELLOW}Deploying Uniswap V3 Factory...${NC}"
FACTORY_ADDRESS=$(cast send --rpc-url $L2_RPC_URL --private-key $DEPLOYER_PRIVATE_KEY --create --value 0  | grep "contractAddress" | cut -d'"' -f4 | head -1)

if [ -z "$FACTORY_ADDRESS" ]; then
    echo -e "${RED}❌ Failed to deploy Uniswap V3 Factory${NC}"
    exit 1
fi

echo "FACTORY_ADDRESS=$FACTORY_ADDRESS" >> data/contracts/addresses.env
echo -e "${GREEN}✅ Uniswap V3 Factory deployed at: $FACTORY_ADDRESS${NC}"

# Deploy Position Manager
echo -e "${YELLOW}Deploying Uniswap V3 Position Manager...${NC}"
POSITION_MANAGER_ADDRESS=$(cast send --rpc-url $L2_RPC_URL --private-key $DEPLOYER_PRIVATE_KEY --create --constructor-args $FACTORY_ADDRESS "0x0000000000000000000000000000000000000000" --value 0  | grep "contractAddress" | cut -d'"' -f4 | head -1)

if [ -z "$POSITION_MANAGER_ADDRESS" ]; then
    echo -e "${RED}❌ Failed to deploy Position Manager${NC}"
    exit 1
fi

echo "POSITION_MANAGER_ADDRESS=$POSITION_MANAGER_ADDRESS" >> data/contracts/addresses.env
echo -e "${GREEN}✅ Position Manager deployed at: $POSITION_MANAGER_ADDRESS${NC}"

# Create a pool (USDC/ETH with 0.3% fee)
echo -e "${YELLOW}Creating USDC/ETH pool...${NC}"
WETH_ADDRESS="0x0000000000000000000000000000000000000000" # Placeholder - would need actual WETH

# Mint some USDC to the deployer for liquidity
echo -e "${YELLOW}Minting USDC for liquidity...${NC}"
cast send --rpc-url $L2_RPC_URL --private-key $DEPLOYER_PRIVATE_KEY $USDC_ADDRESS "mint(address,uint256)" $DEPLOYER_ADDRESS 1000000000000 # 1M USDC with 6 decimals

echo -e "${GREEN}✅ Contract deployment complete!${NC}"
echo -e "${BLUE}📋 Contract addresses saved to data/contracts/addresses.env${NC}"
echo -e "${BLUE}📋 Contract details:${NC}"
echo -e "  • USDC: $USDC_ADDRESS"
echo -e "  • Uniswap V3 Factory: $FACTORY_ADDRESS"  
echo -e "  • Position Manager: $POSITION_MANAGER_ADDRESS"